import axios from "axios"

const token = localStorage.getItem("Token");

const axiosInstance = axios.create({
  baseURL: "http://127.0.0.1.8000/",
  headers:{
    Authorization: token? `Bearer ${token}`: ""
  },
});

export default axiosInstance
