import React, { useContext, useState } from "react";
import uni from './uni.jpg';
import logo from './logo.png';
import { Link, useNavigate } from "react-router-dom";
import AuthContext from './AuthContext';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { setAuth } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/token/", {
        email: email,
        password: password
      });
      const { access } = response.data;

      localStorage.setItem('Token', access);
      setAuth(access);
      console.log('access_token : ', access);
      navigate('/home');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <img src={logo} alt="logo" className="w-32 h-auto mb-4" />
      <img src={uni} alt="background" className="absolute inset-0 object-cover w-full h-full opacity-50" />
      <div className="bg-white p-8 rounded-lg shadow-md w-96">
        <h2 className="text-2xl font-bold mb-6 text-center">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="eg:- abc@gmail.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="eg:- abc#123"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
            />
            <a href="Home.jsx" className="text-sm text-blue-500 hover:underline" id="forgot-pas">Forgot password?</a>
          </div>
          <button type="submit" className="w-full bg-blue-500 text-white font-bold py-2 rounded-md hover:bg-blue-600">LOGIN</button>
          <Link to="/register" className="block text-center mt-4 text-sm text-blue-500 hover:underline">Register now</Link>
        </form>
      </div>
    </div>
  );
};

export default Login;
